(function(){
  window.Chart=function(ctx,config){
    if(!ctx||!ctx.getContext)return;
    const c=ctx.getContext('2d');
    const d=config.data.datasets[0].data;
    const l=config.data.labels;
    c.clearRect(0,0,ctx.width||200,ctx.height||200);
    c.fillStyle='#666';
    c.font='14px sans-serif';
    l.forEach((lab,i)=>{c.fillText(lab+': '+(d[i]||0),10,20*(i+1));});
  };
})();
